import { resolveRef } from "@/registry/lib/utils/resolve-ref";
import type { OpenAPIV3 } from 'openapi-types';
import { useMemo } from "react";

/**
 * 自定义钩子，简化OpenAPI结构的处理
 * @param spec OpenAPI规范对象或null
 * @returns 一组处理OpenAPI结构的工具函数
 */
export function useOpenApi(spec: OpenAPIV3.Document | null) {
  // 当spec为null时，返回空的组件
  const components = spec?.components;

  // 优化引用解析，避免重复计算
  const resolve = useMemo(() => {
    return function resolve<T>(
      obj: T | OpenAPIV3.ReferenceObject | undefined,
      category?: string,
    ): T | null {
      if (!spec || !components) return null;
      return resolveRef<T>(obj, components, category);
    };
  }, [components, spec]);

  /**
   * 获取模式的类型信息
   * @param schema 模式对象
   * @returns 类型信息字符串
   */
  const getSchemaType = (
    schema: OpenAPIV3.SchemaObject | OpenAPIV3.ReferenceObject | undefined,
  ): string => {
    if (!schema) return "未知";

    const resolvedSchema = resolve<OpenAPIV3.SchemaObject>(schema, "schemas");
    if (!resolvedSchema) return "未知";

    let type = resolvedSchema.type || "";

    // 处理数组类型
    if (type === "array" && 'items' in resolvedSchema && resolvedSchema.items) {
      const itemType = getSchemaType(resolvedSchema.items);
      return `${type}<${itemType}>`;
    }

    // 处理对象或复合类型
    if (type === "object" || !type) {
      if (resolvedSchema.properties) {
        return "object";
      }
      if (resolvedSchema.allOf) {
        return "allOf";
      }
      if (resolvedSchema.oneOf) {
        return "oneOf";
      }
      if (resolvedSchema.anyOf) {
        return "anyOf";
      }
    }

    // 处理格式化的类型
    if (resolvedSchema.format) {
      return `${type}(${resolvedSchema.format})`;
    }

    return type || "未知";
  };

  /**
   * 从请求体中获取模式对象
   * @param requestBody 请求体对象
   * @returns 解析后的模式对象
   */
  const getRequestBodySchema = (
    requestBody: OpenAPIV3.RequestBodyObject | OpenAPIV3.ReferenceObject | undefined,
  ): OpenAPIV3.SchemaObject | null => {
    if (!requestBody) return null;

    const resolvedBody = resolve<OpenAPIV3.RequestBodyObject>(
      requestBody,
      "requestBodies",
    );
    if (!resolvedBody || !resolvedBody.content) return null;

    // 尝试获取常见内容类型
    const contentTypes = ["application/json", "application/xml", "*/*"];
    for (const contentType of contentTypes) {
      if (resolvedBody.content[contentType]?.schema) {
        return (
          resolve<OpenAPIV3.SchemaObject>(
            resolvedBody.content[contentType].schema,
            "schemas",
          ) || null
        );
      }
    }

    // 如果找不到常见类型，使用第一个可用类型
    const firstContentType = Object.keys(resolvedBody.content)[0];
    if (firstContentType && resolvedBody.content[firstContentType]?.schema) {
      return (
        resolve<OpenAPIV3.SchemaObject>(
          resolvedBody.content[firstContentType].schema,
          "schemas",
        ) || null
      );
    }

    return null;
  };

  /**
   * 获取模式的属性信息
   * @param schema 模式对象
   * @returns 属性对象集合
   */
  const getSchemaProperties = (
    schema: OpenAPIV3.SchemaObject | OpenAPIV3.ReferenceObject | undefined,
  ) => {
    if (!schema) return {};

    const resolvedSchema = resolve<OpenAPIV3.SchemaObject>(schema, "schemas");
    if (!resolvedSchema) return {};

    // 处理基本对象
    if (resolvedSchema.properties) {
      return resolvedSchema.properties;
    }

    // 处理数组
    if (resolvedSchema.type === "array" && 'items' in resolvedSchema && resolvedSchema.items) {
      const itemsSchema = resolve<OpenAPIV3.SchemaObject>(
        resolvedSchema.items,
        "schemas",
      );
      if (itemsSchema?.properties) {
        return itemsSchema.properties;
      }
    }

    // 处理组合模式
    if (resolvedSchema.allOf) {
      // 合并所有allOf模式的属性
      const mergedProperties = {};
      for (const subSchema of resolvedSchema.allOf) {
        const subProperties = getSchemaProperties(subSchema);
        Object.assign(mergedProperties, subProperties);
      }
      return mergedProperties;
    }

    return {};
  };

  /**
   * 处理参数集合，按位置分组
   * @param parameters 参数对象数组
   * @returns 按位置分组的参数
   */
  const processParameters = (
    parameters: (OpenAPIV3.ParameterObject | OpenAPIV3.ReferenceObject)[] | undefined,
  ) => {
    if (!parameters || parameters.length === 0) {
      return {};
    }

    const result: { [key: string]: (OpenAPIV3.ParameterObject | OpenAPIV3.ReferenceObject)[] } = {
      path: [],
      query: [],
      header: [],
      cookie: [],
    };

    for (const param of parameters) {
      const resolvedParam = resolve<OpenAPIV3.ParameterObject>(param, "parameters");
      if (resolvedParam && resolvedParam.in) {
        const paramIn = resolvedParam.in as keyof typeof result;
        if (paramIn in result && result[paramIn]) {
          result[paramIn].push(resolvedParam);
        }
      }
    }

    return result;
  };

  /**
   * 处理响应对象
   * @param response 响应对象
   * @returns 处理后的响应对象
   */
  const processResponse = (
    response: OpenAPIV3.ResponseObject | OpenAPIV3.ReferenceObject | undefined,
  ) => {
    if (!response) return null;

    const resolvedResponse = resolve<OpenAPIV3.ResponseObject>(response, "responses");
    if (!resolvedResponse) return null;

    const result = {
      description: resolvedResponse.description || "",
      headers: resolvedResponse.headers || {},
      content: resolvedResponse.content || {},
    };

    return result;
  };

  /**
   * 获取模式的约束条件
   * @param schema 模式对象
   * @returns 约束条件对象
   */
  const getSchemaConstraints = (
    schema: OpenAPIV3.SchemaObject | OpenAPIV3.ReferenceObject | undefined,
  ) => {
    if (!schema) return {};

    const resolvedSchema = resolve<OpenAPIV3.SchemaObject>(schema, "schemas");
    if (!resolvedSchema) return {};

    const constraints: { [key: string]: any } = {};

    // 数值约束
    if (typeof resolvedSchema.minimum === "number") {
      constraints.minimum = resolvedSchema.minimum;
    }
    if (typeof resolvedSchema.maximum === "number") {
      constraints.maximum = resolvedSchema.maximum;
    }
    if (typeof resolvedSchema.exclusiveMinimum === "number") {
      constraints.exclusiveMinimum = resolvedSchema.exclusiveMinimum;
    }
    if (typeof resolvedSchema.exclusiveMaximum === "number") {
      constraints.exclusiveMaximum = resolvedSchema.exclusiveMaximum;
    }
    if (typeof resolvedSchema.multipleOf === "number") {
      constraints.multipleOf = resolvedSchema.multipleOf;
    }

    // 字符串约束
    if (typeof resolvedSchema.minLength === "number") {
      constraints.minLength = resolvedSchema.minLength;
    }
    if (typeof resolvedSchema.maxLength === "number") {
      constraints.maxLength = resolvedSchema.maxLength;
    }
    if (resolvedSchema.pattern) {
      constraints.pattern = resolvedSchema.pattern;
    }

    // 数组约束
    if (typeof resolvedSchema.minItems === "number") {
      constraints.minItems = resolvedSchema.minItems;
    }
    if (typeof resolvedSchema.maxItems === "number") {
      constraints.maxItems = resolvedSchema.maxItems;
    }
    if (resolvedSchema.uniqueItems) {
      constraints.uniqueItems = resolvedSchema.uniqueItems;
    }

    // 对象约束
    if (typeof resolvedSchema.minProperties === "number") {
      constraints.minProperties = resolvedSchema.minProperties;
    }
    if (typeof resolvedSchema.maxProperties === "number") {
      constraints.maxProperties = resolvedSchema.maxProperties;
    }

    return constraints;
  };

  /**
   * 获取指定路径和方法的操作对象
   * @param path 路径
   * @param method HTTP方法
   * @returns 操作对象
   */
  const getOperation = (
    path: string,
    method: string,
  ): OpenAPIV3.OperationObject | null => {
    if (!spec || !spec.paths || !spec.paths[path]) {
      return null;
    }

    const pathItem = spec.paths[path] as OpenAPIV3.PathItemObject;
    const operation = pathItem[method.toLowerCase() as keyof OpenAPIV3.PathItemObject];

    if (operation && typeof operation === "object" && "responses" in operation) {
      return operation as OpenAPIV3.OperationObject;
    }

    return null;
  };

  /**
   * 获取服务器列表
   * @returns 服务器对象数组
   */
  const getServers = (): OpenAPIV3.ServerObject[] => {
    return spec?.servers || [];
  };

  /**
   * 按标签分组获取操作
   * @returns 按标签分组的操作对象
   */
  const getOperationsByTag = () => {
    if (!spec || !spec.paths) {
      return {};
    }

    const operations: { [tag: string]: Array<{ path: string; method: string; operation: OpenAPIV3.OperationObject }> } = {};

    Object.entries(spec.paths).forEach(([path, pathItem]) => {
      if (!pathItem || typeof pathItem !== "object") return;

      const methods = ["get", "post", "put", "delete", "patch", "head", "options", "trace"];

      methods.forEach((method) => {
        const operation = pathItem[method as keyof OpenAPIV3.PathItemObject];
        if (operation && typeof operation === "object" && "responses" in operation) {
          const op = operation as OpenAPIV3.OperationObject;
          const tags = op.tags || ["default"];

          tags.forEach((tag) => {
            if (!operations[tag]) {
              operations[tag] = [];
            }
            operations[tag].push({
              path,
              method: method.toUpperCase(),
              operation: op,
            });
          });
        }
      });
    });

    return operations;
  };

  /**
   * 获取所有标签
   * @returns 标签数组
   */
  const getTags = () => {
    if (!spec) return [];
    return spec.tags || [];
  };

  /**
   * 获取API信息
   * @returns API信息对象
   */
  const getInfo = () => {
    return spec?.info || null;
  };

  /**
   * 获取组件定义
   * @returns 组件对象
   */
  const getComponents = () => {
    return spec?.components || null;
  };

  return {
    // 原始数据
    spec,
    components,

    // 解析函数
    resolve,

    // 模式处理
    getSchemaType,
    getSchemaProperties,
    getSchemaConstraints,
    getRequestBodySchema,

    // 参数和响应处理
    processParameters,
    processResponse,

    // 操作处理
    getOperation,
    getOperationsByTag,
    getTags,

    // 服务器和基本信息
    getServers,
    getInfo,
    getComponents,
  };
}

import { useMemo } from 'react';

/**
 * Hook for working with OpenAPI operations
 * @param operation - The operation object
 * @param path - The path string
 * @param method - The HTTP method
 * @returns Operation utilities
 */
export function useOperation(
  operation: any,
  path: string,
  method: string
) {
  return useMemo(() => {
    const getOperationId = () => {
      return operation.operationId || `${method.toLowerCase()}-${path}`;
    };

    const getTags = () => {
      return operation.tags || [];
    };

    const getParameters = () => {
      return operation.parameters || [];
    };

    const getRequestBody = () => {
      return operation.requestBody;
    };

    const getResponses = () => {
      return operation.responses || {};
    };

    const getSecurity = () => {
      return operation.security || [];
    };

    return {
      getOperationId,
      getTags,
      getParameters,
      getRequestBody,
      getResponses,
      getSecurity,
    };
  }, [operation, path, method]);
}

import { useMemo } from 'react';
import { resolveRef } from '../utils/resolve-ref';

/**
 * Hook for working with OpenAPI schemas
 * @param schema - The schema object or reference
 * @param components - The components object containing schemas
 * @returns Resolved schema and utility functions
 */
export function useSchema(
  schema: any,
  components: any
) {
  return useMemo(() => {
    const resolvedSchema = schema.$ref ? resolveRef(schema, components) : schema;

    const getType = () => {
      if (!resolvedSchema || typeof resolvedSchema !== 'object') return 'unknown';
      return resolvedSchema.type || 'object';
    };

    const isRequired = (propertyName: string) => {
      if (!resolvedSchema || typeof resolvedSchema !== 'object') return false;
      return resolvedSchema.required?.includes(propertyName) || false;
    };

    const getProperties = () => {
      if (!resolvedSchema || typeof resolvedSchema !== 'object') return {};
      return resolvedSchema.properties || {};
    };

    return {
      resolvedSchema,
      getType,
      isRequired,
      getProperties,
    };
  }, [schema, components]);
}

"use client";

import React, { createContext, useContext, useState } from "react";
import en from "./locales/en";
import zh from "./locales/zh";

type LocaleStrings = Record<string, string>;
const locales: Record<string, LocaleStrings> = { en, zh };

interface I18nContextProps {
  locale: "en" | "zh";
  setLocale: (locale: "en" | "zh") => void;
  t: (key: string) => string;
}

const I18nContext = createContext<I18nContextProps | undefined>(undefined);

export const I18nProvider: React.FC<{ children: React.ReactNode }> = ({
  children,
}) => {
  const [locale, setLocale] = useState<"en" | "zh">("en");

  const t = (key: string): string => {
    // 直接使用扁平结构查找，如果未找到则返回原key
    return locales[locale]?.[key] || key;
  };

  return (
    <I18nContext.Provider value={{ locale, setLocale, t }}>
      {children}
    </I18nContext.Provider>
  );
};

// 创建一个默认的翻译函数，使用英文作为默认语言
const createDefaultTranslator = (defaultLocale: "en" | "zh" = "en") => {
  return (key: string): string => {
    return locales[defaultLocale]?.[key] || key;
  };
};

// 重构后的 useI18n hook，支持在没有 provider 的情况下使用默认语言
export const useI18n = (): I18nContextProps => {
  const context = useContext(I18nContext);

  // 如果没有 provider，返回默认的 i18n 对象
  if (!context) {
    const defaultT = createDefaultTranslator("en");
    return {
      locale: "en",
      setLocale: () => {
        console.warn("useI18n: setLocale called outside of I18nProvider. Language switching is not available.");
      },
      t: defaultT,
    };
  }

  return context;
};

// 导出一个独立的翻译函数，可以在组件外部使用
export const t = createDefaultTranslator("en");

// 导出一个创建翻译函数的工厂函数
export const createTranslator = (locale: "en" | "zh" = "en") => {
  return createDefaultTranslator(locale);
};

import { resolveRef } from "@/registry/lib/utils/resolve-ref";
import { faker } from '@faker-js/faker';
import type { OpenAPIV3 } from 'openapi-types';

// 配置 faker.js
// 注意：@faker-js/faker 8.x 版本不再支持 setLocale，使用默认语言

/**
 * 根据OpenAPI Schema生成示例数据
 *
 * @param schema 要生成示例的Schema对象
 * @param components 引用解析所需的components
 * @param options 额外配置选项
 * @returns 生成的示例数据
 */
export function generateExample(
  schema: OpenAPIV3.SchemaObject | OpenAPIV3.ReferenceObject,
  components?: OpenAPIV3.ComponentsObject,
  options: {
    maxDepth?: number;
    currentDepth?: number;
    includeReadOnly?: boolean;
    includeWriteOnly?: boolean;
  } = {},
): any {
  // 默认选项
  const {
    maxDepth = 3,
    currentDepth = 0,
    includeReadOnly = true,
    includeWriteOnly = true,
  } = options;

  // 防止过深递归
  if (currentDepth > maxDepth) {
    return {};
  }

  // 解析引用
  let resolvedSchema: OpenAPIV3.SchemaObject | null = null;
  if ("$ref" in schema) {
    resolvedSchema = resolveRef<OpenAPIV3.SchemaObject>(schema, components, "schemas");
    if (!resolvedSchema) {
      console.warn(`无法解析引用: ${schema.$ref}`);
      return {};
    }
  } else {
    resolvedSchema = schema;
  }

  // 如果提供了example，直接使用
  if (resolvedSchema.example !== undefined) {
    return resolvedSchema.example;
  }

  // 如果提供了default，使用默认值
  if (resolvedSchema.default !== undefined) {
    return resolvedSchema.default;
  }

  // 如果有enum，返回第一个枚举值
  if (resolvedSchema.enum && resolvedSchema.enum.length > 0) {
    return resolvedSchema.enum[0];
  }

  // 根据type生成示例
  const type = resolvedSchema.type;

  // 处理复合模式
  if (resolvedSchema.allOf && resolvedSchema.allOf.length > 0) {
    // 合并所有allOf模式
    const mergedExample = {};
    for (const subSchema of resolvedSchema.allOf) {
      const subExample = generateExample(subSchema, components, {
        ...options,
        currentDepth: currentDepth + 1,
      });
      Object.assign(mergedExample, subExample);
    }
    return mergedExample;
  }

  if (resolvedSchema.oneOf && resolvedSchema.oneOf.length > 0) {
    // 使用第一个oneOf模式
    const firstSchema = resolvedSchema.oneOf[0];
    if (firstSchema) {
      return generateExample(firstSchema, components, {
        ...options,
        currentDepth: currentDepth + 1,
      });
    }
  }

  if (resolvedSchema.anyOf && resolvedSchema.anyOf.length > 0) {
    // 使用第一个anyOf模式
    const firstSchema = resolvedSchema.anyOf[0];
    if (firstSchema) {
      return generateExample(firstSchema, components, {
        ...options,
        currentDepth: currentDepth + 1,
      });
    }
  }

  // 根据类型处理
  switch (type) {
    case "string": {
      // 为不同格式生成合适的字符串
      const format = resolvedSchema.format;
      const propName = resolvedSchema.title || '';

      if (format === "date") {
        return faker.date.recent({ days: 30 }).toISOString().split("T")[0]; // YYYY-MM-DD
      } else if (format === "date-time") {
        return faker.date.recent({ days: 30 }).toISOString(); // YYYY-MM-DDThh:mm:ss.sssZ
      } else if (format === "email") {
        return faker.internet.email();
      } else if (format === "uuid") {
        return faker.string.uuid();
      } else if (format === "uri" || format === "url") {
        return faker.internet.url();
      } else if (format === "hostname") {
        return faker.internet.domainName();
      } else if (format === "ipv4") {
        return faker.internet.ipv4();
      } else if (format === "ipv6") {
        return faker.internet.ipv6();
      } else if (format === "password") {
        return faker.internet.password({ length: 12 });
      } else if (format === "byte") {
        return faker.string.alphanumeric(8);
      } else if (format === "binary") {
        return faker.string.binary({ length: 16 });
      } else if (resolvedSchema.pattern) {
        // 对于有模式的，尝试生成符合模式的示例
        return `${faker.lorem.word()}_${faker.string.alphanumeric(6)}`;
      } else {
        // 根据属性名推断类型生成合适的示例
        const lowerPropName = propName.toLowerCase();

        // 基础信息类
        if (lowerPropName.includes('name') || lowerPropName.includes('title')) {
          return faker.person.fullName();
        } else if (lowerPropName.includes('email')) {
          return faker.internet.email();
        } else if (lowerPropName.includes('phone') || lowerPropName.includes('mobile') || lowerPropName.includes('tel')) {
          return faker.phone.number();
        } else if (lowerPropName.includes('username') || lowerPropName.includes('user_name')) {
          return faker.internet.userName();
        } else if (lowerPropName.includes('password') || lowerPropName.includes('passwd')) {
          return faker.internet.password({ length: 12 });
        }

        // 姓名相关
        else if (lowerPropName.includes('first') && (lowerPropName.includes('name') || lowerPropName.includes('_name'))) {
          return faker.person.firstName();
        } else if (lowerPropName.includes('last') && (lowerPropName.includes('name') || lowerPropName.includes('_name'))) {
          return faker.person.lastName();
        } else if (lowerPropName.includes('full') && lowerPropName.includes('name')) {
          return faker.person.fullName();
        } else if (lowerPropName.includes('middle') && lowerPropName.includes('name')) {
          return faker.person.middleName();
        }

        // 地址相关
        else if (lowerPropName.includes('address')) {
          return faker.location.streetAddress({ useFullAddress: true });
        } else if (lowerPropName.includes('street')) {
          return faker.location.street();
        } else if (lowerPropName.includes('city')) {
          return faker.location.city();
        } else if (lowerPropName.includes('state') || lowerPropName.includes('province')) {
          return faker.location.state();
        } else if (lowerPropName.includes('country')) {
          return faker.location.country();
        } else if (lowerPropName.includes('zip') || lowerPropName.includes('postal')) {
          return faker.location.zipCode();
        }

        // 公司相关
        else if (lowerPropName.includes('company') || lowerPropName.includes('organization')) {
          return faker.company.name();
        } else if (lowerPropName.includes('department')) {
          return faker.commerce.department();
        } else if (lowerPropName.includes('position') || lowerPropName.includes('job') || lowerPropName.includes('role')) {
          return faker.person.jobTitle();
        }

        // 网络相关
        else if (lowerPropName.includes('url') || lowerPropName.includes('website') || lowerPropName.includes('link')) {
          return faker.internet.url();
        } else if (lowerPropName.includes('domain')) {
          return faker.internet.domainName();
        } else if (lowerPropName.includes('ip')) {
          return faker.internet.ipv4();
        }

        // 媒体相关
        else if (lowerPropName.includes('avatar') || lowerPropName.includes('image') || lowerPropName.includes('photo')) {
          return faker.image.avatar();
        } else if (lowerPropName.includes('color') || lowerPropName.includes('colour')) {
          return faker.color.human();
        }

        // 商业相关
        else if (lowerPropName.includes('product')) {
          return faker.commerce.productName();
        } else if (lowerPropName.includes('price') || lowerPropName.includes('amount') || lowerPropName.includes('cost')) {
          return faker.commerce.price();
        } else if (lowerPropName.includes('currency')) {
          return faker.finance.currencyCode();
        } else if (lowerPropName.includes('sku') || lowerPropName.includes('code')) {
          return faker.string.alphanumeric(8).toUpperCase();
        }

        // 内容相关
        else if (lowerPropName.includes('description') || lowerPropName.includes('desc')) {
          return faker.lorem.sentence();
        } else if (lowerPropName.includes('comment') || lowerPropName.includes('note') || lowerPropName.includes('remark')) {
          return faker.lorem.sentence();
        } else if (lowerPropName.includes('content') || lowerPropName.includes('text') || lowerPropName.includes('body')) {
          return faker.lorem.paragraph();
        } else if (lowerPropName.includes('summary') || lowerPropName.includes('excerpt')) {
          return faker.lorem.sentence();
        }

        // 状态和分类
        else if (lowerPropName.includes('status')) {
          return faker.helpers.arrayElement(['active', 'inactive', 'pending', 'completed', 'draft', 'published']);
        } else if (lowerPropName.includes('type')) {
          return faker.helpers.arrayElement(['standard', 'premium', 'basic', 'advanced']);
        } else if (lowerPropName.includes('tag') || lowerPropName.includes('category')) {
          return faker.helpers.arrayElement(['technology', 'business', 'design', 'marketing', 'finance', 'education']);
        } else if (lowerPropName.includes('priority')) {
          return faker.helpers.arrayElement(['low', 'medium', 'high', 'urgent']);
        }

        // 个人信息
        else if (lowerPropName.includes('age')) {
          return faker.number.int({ min: 18, max: 80 }).toString();
        } else if (lowerPropName.includes('gender') || lowerPropName.includes('sex')) {
          return faker.person.gender();
        } else if (lowerPropName.includes('birthday') || lowerPropName.includes('birth')) {
          return faker.date.birthdate().toISOString().split('T')[0];
        }

        // ID 相关
        else if (lowerPropName.includes('id') && lowerPropName.length <= 5) {
          return faker.string.numeric(8);
        } else if (lowerPropName.includes('uuid') || lowerPropName.includes('guid')) {
          return faker.string.uuid();
        }

        // 其他常见字段
        else if (lowerPropName.includes('version')) {
          return faker.system.semver();
        } else if (lowerPropName.includes('count') || lowerPropName.includes('number') || lowerPropName.includes('num')) {
          return faker.number.int({ min: 1, max: 100 }).toString();
        } else if (lowerPropName.includes('size')) {
          return faker.helpers.arrayElement(['small', 'medium', 'large']);
        } else if (lowerPropName.includes('format')) {
          return faker.helpers.arrayElement(['json', 'xml', 'csv', 'pdf']);
        }

        else {
          // 默认生成随机词语
          return faker.lorem.words(2);
        }
      }
    }

    case "number":
    case "integer": {
      // 处理数字范围
      if (
        resolvedSchema.minimum !== undefined &&
        resolvedSchema.maximum !== undefined
      ) {
        const min = resolvedSchema.minimum;
        const max = resolvedSchema.maximum;
        return type === "integer"
          ? faker.number.int({ min, max })
          : faker.number.float({ min, max, fractionDigits: 2 });
      } else if (resolvedSchema.minimum !== undefined) {
        const min = resolvedSchema.minimum;
        const max = min + 1000;
        return type === "integer"
          ? faker.number.int({ min, max })
          : faker.number.float({ min, max, fractionDigits: 2 });
      } else if (resolvedSchema.maximum !== undefined) {
        const max = resolvedSchema.maximum;
        const min = Math.max(0, max - 1000);
        return type === "integer"
          ? faker.number.int({ min, max })
          : faker.number.float({ min, max, fractionDigits: 2 });
      } else {
        return type === "integer"
          ? faker.number.int({ min: 1, max: 100 })
          : faker.number.float({ min: 1, max: 100, fractionDigits: 2 });
      }
    }

    case "boolean":
      return faker.datatype.boolean();

    case "array": {
      // 检查items定义
      if (!resolvedSchema.items) {
        return [];
      }

      // 生成 1-3 个元素的数组作为示例
      const length = faker.number.int({ min: 1, max: 3 });
      const result = [];

      for (let i = 0; i < length; i++) {
        const itemExample = generateExample(resolvedSchema.items, components, {
          ...options,
          currentDepth: currentDepth + 1,
        });
        result.push(itemExample);
      }

      return result;
    }

    case "object":
    default: {
      // 对于对象或未指定类型但有properties的情况
      const result: Record<string, any> = {};

      if (resolvedSchema.properties) {
        // 遍历所有属性
        for (const [propName, propSchema] of Object.entries(
          resolvedSchema.properties,
        )) {
          // 跳过只读/只写属性（如果配置了）
          if (
            ((propSchema as OpenAPIV3.SchemaObject).readOnly && !includeReadOnly) ||
            ((propSchema as OpenAPIV3.SchemaObject).writeOnly && !includeWriteOnly)
          ) {
            continue;
          }

          // 创建增强的 schema，包含属性名信息用于智能推断
          const enhancedSchema = {
            ...(propSchema as OpenAPIV3.SchemaObject),
            title: (propSchema as OpenAPIV3.SchemaObject).title || propName,
          };

          // 递归生成属性值
          result[propName] = generateExample(enhancedSchema, components, {
            ...options,
            currentDepth: currentDepth + 1,
          });
        }
      }

      return result;
    }
  }
}

import type { OpenAPIV3 } from 'openapi-types';

/**
 * 解析OpenAPI中的引用对象
 * @param obj 可能是引用对象的数据
 * @param components OpenAPI组件定义
 * @param category 可选的组件类别(schemas, parameters等)
 * @returns 解析后的对象或原始对象
 */
export function resolveRef<T>(
  obj: T | OpenAPIV3.ReferenceObject | undefined,
  components?: OpenAPIV3.ComponentsObject,
  category?: string,
): T | null {
  if (!obj) return null;

  // 检查是否是引用对象
  if (typeof obj === "object" && obj !== null && "$ref" in obj) {
    const refObj = obj as OpenAPIV3.ReferenceObject;
    const refPath = refObj.$ref;

    // 处理标准格式的引用 #/components/{category}/{name}
    const refMatch = refPath.match(/^#\/components\/([^/]+)\/(.+)$/);

    if (refMatch && components) {
      const [, refCategory, refName] = refMatch;

      // 检查refName是否存在
      if (!refName) {
        console.warn(`引用名称为空: ${refPath}`);
        return null;
      }

      // 如果指定了类别，并且与引用类别不一致，则发出警告
      if (category && refCategory !== category) {
        console.warn(`引用类别 ${refCategory} 与期望类别 ${category} 不一致`);
      }

      // 根据类别获取组件集合
      const componentCollection =
        components[refCategory as keyof OpenAPIV3.ComponentsObject];

      if (componentCollection && typeof componentCollection === "object") {
        // 解析引用对象
        const resolved = componentCollection[refName];

        if (resolved) {
          // 检查是否是嵌套引用，如果是则递归解析
          if (
            typeof resolved === "object" &&
            resolved !== null &&
            "$ref" in resolved
          ) {
            return resolveRef<T>(
              resolved as OpenAPIV3.ReferenceObject,
              components,
              category,
            );
          }
          return resolved as unknown as T;
        }
      }

      console.warn(`找不到引用 ${refPath}`);
      return null;
    }

    console.warn(`不支持的引用格式 ${refPath}`);
    return null;
  }

  // 如果不是引用对象，则返回原始对象
  return obj as T;
}

/**
 * Check if a schema is a reference object
 */
export function isReferenceObject(obj: any): obj is { $ref: string } {
  return obj && typeof obj === 'object' && '$ref' in obj;
}

/**
 * Check if a schema is a schema object
 */
export function isSchemaObjectNotRef(obj: any): boolean {
  return obj && typeof obj === 'object' && !('$ref' in obj);
}

/**
 * Get the type of a schema
 */
export function getSchemaType(schema: any): string {
  if (schema.type) {
    return schema.type;
  }
  if (schema.allOf) {
    return 'allOf';
  }
  if (schema.oneOf) {
    return 'oneOf';
  }
  if (schema.anyOf) {
    return 'anyOf';
  }
  return 'object';
}

/**
 * Check if a property is required in a schema
 */
export function isPropertyRequired(schema: any, propertyName: string): boolean {
  return schema.required?.includes(propertyName) || false;
}

/**
 * Type guards and utility functions for OpenAPI types
 */

export function isParameterObject(obj: any): boolean {
  return obj && typeof obj === 'object' && 'name' in obj && 'in' in obj;
}

export function isRequestBodyObject(obj: any): boolean {
  return obj && typeof obj === 'object' && 'content' in obj;
}

export function isResponseObject(obj: any): boolean {
  return obj && typeof obj === 'object' && 'description' in obj;
}

export function isHeaderObject(obj: any): boolean {
  return obj && typeof obj === 'object' && !('name' in obj) && !('in' in obj);
}

export function isSchemaObject(obj: any): boolean {
  return obj && typeof obj === 'object' && 'type' in obj;
}

export function isComponentObject(obj: any): boolean {
  return obj && typeof obj === 'object' && !('$ref' in obj) && !('type' in obj);
}

export function isLinkObject(obj: any): boolean {
  return obj && typeof obj === 'object' && ('operationRef' in obj || 'operationId' in obj);
}

export function isExampleObject(obj: any): boolean {
  return obj && typeof obj === 'object' && ('value' in obj || 'externalValue' in obj);
}
